package switchCase;

public class CheckVowel {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	  char ch = 'o';
		
		switch (ch) {
		case  'a':
			System.out.println("Vowel");
			break;
		case 'e':
			System.out.println("Vowel");
			break;
		case 'i':
			System.out.println("Vowel");
			break;
		case 'o':
			System.out.println("Vowel");
			break;
		case 'u':
			System.out.println("Vowel");
			break;
			

		default:
			System.out.println("Not a vowel");
			break;
		}
		
		

	}

}
